import React, { useState } from 'react';

const AuthForm = ({ onSuccess }) => {
  const [isLogin, setIsLogin]       = useState(true);
  const [formData, setFormData]     = useState({ username:'', email:'', password:'', confirmPassword:'' });
  const [error, setError]           = useState('');
  const [loading, setLoading]       = useState(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      if (isLogin) {
        // 1️⃣ Login: obtengo todos y busco por email (simplificado)
        const res = await fetch('/api/usuarios');
        const list = await res.json();
        const user = list.find(u => u.email === formData.email);
        if (!user) throw new Error('Usuario no encontrado');
        onSuccess(user);
      } else {
        // 2️⃣ Registro: creo nuevo usuario
        if (formData.password !== formData.confirmPassword) {
          throw new Error('Las contraseñas no coinciden');
        }
        const payload = {
          nombre: formData.username,
          email:  formData.email,
          gpu1:   '',
          gpu2:   ''
        };
        const res = await fetch('/api/usuarios', {
          method: 'POST',
          headers: { 'Content-Type':'application/json' },
          body: JSON.stringify(payload)
        });
        if (!res.ok) throw new Error('Error al registrar');
        const newUser = await res.json();
        onSuccess(newUser);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-8 bg-gray-800 rounded">
      <h2 className="text-2xl mb-4">{isLogin ? 'Iniciar sesión' : 'Registrarse'}</h2>
      {error && <p className="text-red-400 mb-2">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        {!isLogin && (
          <input
            name="username"
            placeholder="Nombre de usuario"
            value={formData.username}
            onChange={handleChange}
            className="w-full p-2 bg-gray-700 rounded"
            required
          />
        )}
        <input
          name="email"
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 bg-gray-700 rounded"
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Contraseña"
          value={formData.password}
          onChange={handleChange}
          className="w-full p-2 bg-gray-700 rounded"
          required
        />
        {!isLogin && (
          <input
            name="confirmPassword"
            type="password"
            placeholder="Confirma contraseña"
            value={formData.confirmPassword}
            onChange={handleChange}
            className="w-full p-2 bg-gray-700 rounded"
            required
          />
        )}
        <button
          type="submit"
          disabled={loading}
          className="w-full py-2 bg-purple-600 hover:bg-purple-700 rounded text-white"
        >
          {loading ? 'Procesando...' : isLogin ? 'Entrar' : 'Registrarse'}
        </button>
      </form>
      <button
        onClick={() => { setIsLogin(!isLogin); setError(''); }}
        className="mt-4 text-sm text-gray-400 hover:text-white"
      >
        {isLogin ? '¿No tienes cuenta? Regístrate' : '¿Ya tienes cuenta? Inicia sesión'}
      </button>
    </div>
  );
};

export default AuthForm;

