import React, { useState } from 'react';
import { users } from '../../mock/users';

const AuthForm = ({ onSuccess }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    if (isLogin) {
      const user = users.find(u => 
        (u.email === formData.email || u.username === formData.email) && 
        u.password === formData.password
      );

      if (user) {
        onSuccess({
          username: user.username,
          email: user.email
        });
        return;
      }
      setError('Credenciales incorrectas');
    } else {
      // Validación de registro
      if (formData.password !== formData.confirmPassword) {
        setError('Las contraseñas no coinciden');
        return;
      }

      if (formData.password.length < 6) {
        setError('La contraseña debe tener al menos 6 caracteres');
        return;
      }

      const userExists = users.some(u => 
        u.email === formData.email || u.username === formData.username
      );

      if (userExists) {
        setError('El usuario o email ya están registrados');
        return;
      }

      // Registrar nuevo usuario
      const newUser = {
        id: users.length + 1,
        username: formData.username,
        email: formData.email,
        password: formData.password
      };

      users.push(newUser);
      setSuccessMessage('¡Registro exitoso! Por favor inicia sesión');
      setIsLogin(true);
      setFormData({
        username: '',
        email: '',
        password: '',
        confirmPassword: ''
      });
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-900 to-purple-900 p-4">
      <form 
        onSubmit={handleSubmit}
        className="w-full max-w-md bg-gray-800 bg-opacity-50 backdrop-blur-lg rounded-xl p-8 shadow-2xl border border-purple-500 border-opacity-30"
      >
        <h2 className="text-2xl font-bold text-white mb-6 text-center">
          {isLogin ? 'Iniciar Sesión' : 'Registrarse'}
        </h2>

        {error && (
          <div className="mb-4 p-3 bg-red-900 bg-opacity-50 text-red-200 rounded-lg text-sm">
            {error}
          </div>
        )}

        {successMessage && (
          <div className="mb-4 p-3 bg-green-900 bg-opacity-50 text-green-200 rounded-lg text-sm">
            {successMessage}
          </div>
        )}

        {!isLogin && (
          <div className="mb-4">
            <label className="block text-gray-300 text-sm mb-2">Nombre de Usuario</label>
            <input
              type="text"
              className="w-full bg-gray-700 bg-opacity-50 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500 transition"
              placeholder="Tu nombre gamer"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
            />
          </div>
        )}

        <div className="mb-4">
          <label className="block text-gray-300 text-sm mb-2">
            {isLogin ? 'Email o Usuario' : 'Email'}
          </label>
          <input
            type={isLogin ? 'text' : 'email'}
            className="w-full bg-gray-700 bg-opacity-50 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500 transition"
            placeholder={isLogin ? 'tu@email.com o usuario' : 'tu@email.com'}
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-300 text-sm mb-2">Contraseña</label>
          <input
            type="password"
            className="w-full bg-gray-700 bg-opacity-50 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500 transition"
            placeholder="••••••••"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
          />
        </div>

        {!isLogin && (
          <div className="mb-6">
            <label className="block text-gray-300 text-sm mb-2">Confirmar Contraseña</label>
            <input
              type="password"
              className="w-full bg-gray-700 bg-opacity-50 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500 transition"
              placeholder="Repite tu contraseña"
              value={formData.confirmPassword}
              onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
              required
            />
          </div>
        )}

        <button
          type="submit"
          className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-3 px-4 rounded-lg transition-all duration-300 transform hover:scale-[1.02] shadow-lg mb-4"
        >
          {isLogin ? 'Acceder' : 'Registrarse'}
        </button>

        <button
          type="button"
          onClick={() => {
            setIsLogin(!isLogin);
            setError('');
            setSuccessMessage('');
          }}
          className="w-full text-center text-gray-400 hover:text-purple-400 text-sm transition-colors"
        >
          {isLogin ? '¿No tienes cuenta? Regístrate' : '¿Ya tienes cuenta? Inicia sesión'}
        </button>
      </form>
    </div>
  );
};

export default AuthForm;

