import React, { useState } from 'react';

const GPUComparison = ({ gpus, onCompare }) => {
  const [selectedGPU1, setSelectedGPU1] = useState(null);
  const [selectedGPU2, setSelectedGPU2] = useState(null);

  const handleCompare = () => {
    if (selectedGPU1 && selectedGPU2) {
      onCompare([selectedGPU1, selectedGPU2]);
    }
  };

  return (
    <div className="container mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold mb-12 text-center bg-gradient-to-r from-purple-400 to-pink-600 bg-clip-text text-transparent">
        Comparador de GPUs NVIDIA RTX 2000
      </h1>

      <div className="max-w-4xl mx-auto bg-gray-800 rounded-xl p-8 border border-gray-700 shadow-2xl">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
          <div>
            <label className="block text-gray-300 mb-2">Selecciona primera GPU</label>
            <select
              className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              onChange={(e) => setSelectedGPU1(parseInt(e.target.value))}
            >
              <option value="">-- Seleccionar --</option>
              {gpus.map(gpu => (
                <option key={gpu.id} value={gpu.id}>{gpu.model}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-gray-300 mb-2">Selecciona segunda GPU</label>
            <select
              className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              onChange={(e) => setSelectedGPU2(parseInt(e.target.value))}
            >
              <option value="">-- Seleccionar --</option>
              {gpus.map(gpu => (
                <option key={gpu.id} value={gpu.id}>{gpu.model}</option>
              ))}
            </select>
          </div>
        </div>

        <button
          onClick={handleCompare}
          disabled={!selectedGPU1 || !selectedGPU2}
          className={`w-full py-3 px-6 rounded-lg font-medium text-white transition-all duration-300 ${
            selectedGPU1 && selectedGPU2 
              ? 'bg-purple-600 hover:bg-purple-700 transform hover:scale-[1.02] shadow-lg' 
              : 'bg-gray-600 cursor-not-allowed'
          }`}
        >
          Comparar GPUs
        </button>
      </div>
    </div>
  );
};

export default GPUComparison;

