import React, { useState } from 'react';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('gpus');
  const [gpus, setGpus] = useState([]);
  const [users, setUsers] = useState([]);
  const [editingItem, setEditingItem] = useState(null);

  const handleAddGPU = () => {
    // Lógica para añadir nueva GPU
  };

  const handleEditGPU = (gpu) => {
    setEditingItem(gpu);
  };

  const handleDeleteGPU = (id) => {
    // Lógica para eliminar GPU
  };

  const handleAddUser = () => {
    // Lógica para añadir nuevo usuario
  };

  const handleEditUser = (user) => {
    setEditingItem(user);
  };

  const handleDeleteUser = (id) => {
    // Lógica para eliminar usuario
  };

  return (
    <div className="container mx-auto px-4 py-12">
      <h2 className="text-3xl font-bold mb-8 text-center bg-gradient-to-r from-purple-400 to-pink-600 bg-clip-text text-transparent">
        Panel de Administración
      </h2>

      <div className="bg-gray-800 bg-opacity-50 backdrop-blur-lg rounded-xl overflow-hidden border border-gray-700">
        <div className="flex border-b border-gray-700">
          <button
            className={`px-6 py-3 font-medium transition-all ${activeTab === 'gpus' ? 'text-purple-400 border-b-2 border-purple-400' : 'text-gray-400 hover:text-white'}`}
            onClick={() => setActiveTab('gpus')}
          >
            Gestión de GPUs
          </button>
          <button
            className={`px-6 py-3 font-medium transition-all ${activeTab === 'users' ? 'text-purple-400 border-b-2 border-purple-400' : 'text-gray-400 hover:text-white'}`}
            onClick={() => setActiveTab('users')}
          >
            Gestión de Usuarios
          </button>
        </div>

        <div className="p-6">
          {activeTab === 'gpus' ? (
            <div>
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-xl font-semibold text-white">Tarjetas Gráficas</h3>
                <button
                  onClick={handleAddGPU}
                  className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-all"
                >
                  Añadir GPU
                </button>
              </div>

              <div className="space-y-4">
                {gpus.map(gpu => (
                  <div key={gpu.id} className="bg-gray-700 bg-opacity-50 p-4 rounded-lg flex justify-between items-center">
                    <div>
                      <h4 className="font-medium text-white">{gpu.model}</h4>
                      <p className="text-sm text-gray-300">{gpu.vram} • ${gpu.price}</p>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEditGPU(gpu)}
                        className="text-gray-300 hover:text-purple-400 transition-colors"
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => handleDeleteGPU(gpu.id)}
                        className="text-gray-300 hover:text-red-400 transition-colors"
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div>
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-xl font-semibold text-white">Usuarios</h3>
                <button
                  onClick={handleAddUser}
                  className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-all"
                >
                  Añadir Usuario
                </button>
              </div>

              <div className="space-y-4">
                {users.map(user => (
                  <div key={user.id} className="bg-gray-700 bg-opacity-50 p-4 rounded-lg flex justify-between items-center">
                    <div>
                      <h4 className="font-medium text-white">{user.username}</h4>
                      <p className="text-sm text-gray-300">{user.email}</p>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEditUser(user)}
                        className="text-gray-300 hover:text-purple-400 transition-colors"
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => handleDeleteUser(user.id)}
                        className="text-gray-300 hover:text-red-400 transition-colors"
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {editingItem && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center p-4 z-50">
          <div className="bg-gray-800 rounded-xl p-6 w-full max-w-md border border-purple-500 border-opacity-30">
            <h3 className="text-xl font-bold text-white mb-4">
              {activeTab === 'gpus' ? 'Editar GPU' : 'Editar Usuario'}
            </h3>

            <form>
              {/* Formulario dinámico según el tipo de edición */}
              <div className="space-y-4">
                {activeTab === 'gpus' ? (
                  <>
                    <div>
                      <label className="block text-gray-300 mb-1">Modelo</label>
                      <input
                        type="text"
                        className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                        defaultValue={editingItem.model}
                      />
                    </div>
                    <div>
                      <label className="block text-gray-300 mb-1">VRAM</label>
                      <input
                        type="text"
                        className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                        defaultValue={editingItem.vram}
                      />
                    </div>
                  </>
                ) : (
                  <>
                    <div>
                      <label className="block text-gray-300 mb-1">Nombre</label>
                      <input
                        type="text"
                        className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                        defaultValue={editingItem.username}
                      />
                    </div>
                    <div>
                      <label className="block text-gray-300 mb-1">Email</label>
                      <input
                        type="email"
                        className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                        defaultValue={editingItem.email}
                      />
                    </div>
                  </>
                )}

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => setEditingItem(null)}
                    className="px-4 py-2 bg-gray-600 hover:bg-gray-500 text-white rounded-lg transition-colors"
                  >
                    Cancelar
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-purple-600 hover:bg-purple-500 text-white rounded-lg transition-colors"
                  >
                    Guardar
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;

// DONE
