import React, { useState } from 'react';

const AuthForm = ({ onSuccess }) => {
  const [formData, setFormData] = useState({ nombre: '', email: '' });
  const [error, setError]       = useState('');
  const [loading, setLoading]   = useState(false);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!formData.nombre || !formData.email) {
      setError('Completa ambos campos');
      return;
    }
    setLoading(true);
    try {
      const res = await fetch('/api/usuarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nombre: formData.nombre,
          email: formData.email,
          gpu1: '',
          gpu2: ''
        })
      });
      if (!res.ok) throw new Error('Error registrando usuario');
      const user = await res.json();
      onSuccess(user);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-8 bg-gray-800 rounded">
      <h2 className="text-2xl mb-4">Reg√≠strate</h2>
      {error && <p className="text-red-400 mb-2">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="nombre"
          placeholder="Tu nombre"
          value={formData.nombre}
          onChange={handleChange}
          className="w-full p-2 bg-gray-700 rounded"
        />
        <input
          name="email"
          type="email"
          placeholder="Tu email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 bg-gray-700 rounded"
        />
        <button
          type="submit"
          disabled={loading}
          className="w-full py-2 bg-purple-600 hover:bg-purple-700 rounded text-white"
        >
          {loading ? 'Registrando...' : 'Registrarme'}
        </button>
      </form>
    </div>
  );
};

export default AuthForm;
