// src/App.js

import { useState, useEffect } from 'react';
import NavBar from './components/Nav/NavBar';
import AuthForm from './components/Auth/AuthForm';
import GPUComparison from './components/Compare/GPUComparison';
import GPUCard from './components/Compare/GPUCard';
import ComparisonResult from './components/Compare/ComparisonResult';
import GPUCatalog from './components/Catalog/GPUCatalog';
import AdminPanel from './components/Admin/AdminPanel';
// import { gpus } from './mock/gpus';  // ‚Üê eliminamos el mock

const App = () => {
  // üì¶ Datos que vienen del backend
  const [gpus, setGpus]             = useState([]);
  const [currentView, setCurrentView] = useState('auth');
  const [user, setUser]             = useState(null);
  const [selectedGPUs, setSelectedGPUs] = useState([]);

  // üöÄ Al arrancar, cargamos las GPUs desde /api/gpus
  useEffect(() => {
    fetch('/api/gpus') 
      .then(res => res.json())
      .then(setGpus)
      .catch(err => console.error('Error cargando GPUs:', err));
  }, []);

  // üîê Cuando AuthForm llama onSuccess, recibe el usuario y avanza
  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentView('main');
  };

  // üéØ Maneja el evento de comparaci√≥n
  const handleCompare = (gpuIds) => {
    setSelectedGPUs(gpuIds);
    setCurrentView('compare');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {user && <NavBar userName={user.username} />}

      <main className="pt-16">
        {currentView === 'auth' && (
          // AuthForm debe hacer POST /api/usuarios y luego onSuccess(userData)
          <AuthForm onSuccess={handleLogin} />
        )}

        {currentView === 'main' && (
          <GPUComparison gpus={gpus} onCompare={handleCompare} />
        )}

        {currentView === 'compare' && (
          <div className="container mx-auto px-4 py-8">
            <h2 className="text-3xl font-bold mb-8 text-center bg-gradient-to-r from-purple-400 to-pink-600 bg-clip-text text-transparent">
              Comparaci√≥n de GPUs
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {selectedGPUs.map(id => {
                const gpu = gpus.find(g => g.id === id);
                return <GPUCard key={gpu.id} gpu={gpu} />;
              })}
            </div>
            <ComparisonResult
              gpu1={gpus.find(g => g.id === selectedGPUs[0])}
              gpu2={gpus.find(g => g.id === selectedGPUs[1])}
            />
          </div>
        )}

        {currentView === 'catalog' && <GPUCatalog gpus={gpus} />}
        {currentView === 'admin' && <AdminPanel />}
      </main>
    </div>
  );
};

export default App;
